"use strict";

var _ = require('underscore');

module.exports = {

  sidebar: null,

  register: function (sidebar) {

    this.sidebar = sidebar;
    this.sidebar.on( 'warn', _.bind( this.warn, this ) );
    this.sidebar.on( 'err', _.bind( this.error, this ) );
  },

  warn: function (msg) {
    console.warn(msg);
  },

  error: function (msg) {
    console.error(msg);
  }
};